  /* ç§»é™¤è¾¹æ¡†å’ŒèƒŒæ™¯è‰²ï¼Œèå…¥é¡µé¢ */
}

/* --- 1. é»‘èƒ¶å”±ç‰‡ (è¶…å†™å®ç‰©ç†è´¨æ„Ÿ) --- */
.vinyl-wrapper {
  position: relative;
  width: var(--vinyl-size);
  height: var(--vinyl-size);
  margin-bottom: 50px;
  cursor: pointer;
  z-index: 10;
  transition: transform 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
}

.vinyl-wrapper:active {
  transform: scale(0.97);
}

/* å”±ç‰‡æœ¬ä½“ */
.vinyl-disc {
  width: 100%;
  height: 100%;
  border-radius: 50%;
  background: #111;
  /* æ¨¡æ‹Ÿé»‘èƒ¶çš„å¾®ç»†æ²Ÿæ§½çº¹ç† */
  background-image: repeating-radial-gradient(
    #111 0, 
    #111 2px, 
    #1a1a1a 3px, 
    #1a1a1a 4px
  );
  position: relative;
  /* é»˜è®¤çŠ¶æ€çš„æŠ•å½±ï¼šå¹²å‡€ã€æ·±é‚ƒ */
  box-shadow: var(--shadow-static);
  transition: box-shadow 0.6s ease;
  animation: spin 8s linear infinite;
  animation-play-state: paused;
}

/* å”±ç‰‡çš„é«˜å…‰åå°„ (ç‰©ç†å…‰æ³½) - å…³é”®ç‚¹ */
.vinyl-disc::before {
  content: '';
  position: absolute;
  top: 0; left: 0; right: 0; bottom: 0;
  border-radius: 50%;
  /* ä½¿ç”¨é”¥å½¢æ¸å˜æ¨¡æ‹Ÿå„å‘å¼‚æ€§åå…‰ */
  background: conic-gradient(
    from 45deg,
    rgba(255,255,255,0) 0%,
    rgba(255,255,255,0.15) 20%,
    rgba(255,255,255,0) 40%,
    rgba(255,255,255,0) 50%,
    rgba(255,255,255,0.1) 70%,
    rgba(255,255,255,0) 90%
  );
  pointer-events: none;
  z-index: 2;
}

/* å°é¢è´´å›¾åŒº */
.vinyl-disc::after {
  content: '';
  position: absolute;
  top: 50%; left: 50%;
  transform: translate(-50%, -50%);
  width: 42%; height: 42%;
  background: url('/music/cover.jpg') no-repeat center center;
  background-size: cover;
  border-radius: 50%;
  /* å°é¢è¾¹ç¼˜çš„ç»†èŠ‚å¤„ç† */
  border: 6px solid #1a1a1a;
  box-shadow: inset 0 0 15px rgba(0,0,0,0.5); 
  z-index: 3;
}

/* æ’­æ”¾æ—¶çš„åŠ¨æ€æ•ˆæœ */
.vinyl-disc.playing {
  animation-play-state: running;
  /* æ’­æ”¾æ—¶ï¼šé˜´å½±å˜å¤§ï¼Œä¸”å¸¦æœ‰ä¸€ç‚¹é«˜çº§çš„è“ç´«è‰²æ¼«å°„å…‰ */
  box-shadow: var(--shadow-active);
}

@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

/* --- 2. æ§åˆ¶åŒºä¸æ’ç‰ˆ --- */
.controls-area {
  width: 100%;
  text-align: center;
  position: relative;
  z-index: 20;
  display: flex;
  flex-direction: column;
  align-items: center;
}

/* åŠ¨æ€å¯è§†åŒ–å£°æ³¢ */
.visualizer {
  display: flex;
  align-items: flex-end;
  justify-content: center;
  height: 24px;
  gap: 5px;
  margin-bottom: 20px;
}

.bar {
  width: 5px;
  background: var(--text-secondary); /* é»˜è®¤ç°è‰² */
  border-radius: 10px;
  transition: all 0.3s;
  height: 4px; /* é»˜è®¤é™æ­¢ */
}

/* æ’­æ”¾æ—¶çš„å£°æ³¢åŠ¨ç”» */
.visualizer.active .bar {
  /* æ¿€æ´»æ—¶å˜æˆç‚«é…·æ¸å˜è‰² */
  background: var(--accent-gradient);
  animation: bounce 1s infinite ease-in-out;
}
.bar:nth-child(1) { animation-delay: 0.0s; }
.bar:nth-child(2) { animation-delay: 0.2s; }
.bar:nth-child(3) { animation-delay: 0.4s; }
.bar:nth-child(4) { animation-delay: 0.1s; }
.bar:nth-child(5) { animation-delay: 0.3s; }

@keyframes bounce {
  0%, 100% { height: 6px; }
  50% { height: 24px; }
}

/* æ­Œåè®¾è®¡ */
.song-title {
  font-size: 2rem;
  font-weight: 700;
  color: var(--text-primary);
